return msg;

const { group_name, scene_name } = msg.payload.data;


msg.payload.text = `${group_name} set to ${scene_name} at: ${msg.dateString}`;


return msg;

const dayPhase = msg.day_phase.payload

const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
