return msg;

const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec"
];

const d = new Date();
const month = months[d.getUTCMonth()];
const day = d.getDay();
const hour = d.getHours() < 10 ? `0${d.getHours()}` : d.getHours();
const min = d.getMinutes() < 10 ? `0${d.getMinutes()}` : d.getMinutes();

msg.dateString = `${month} ${day}, ${hour}:${min}`;

return msg;

const { group_name, state } = msg.payload;

return {
    payload: {
        text: `${group_name} to ${state} at: ${msg.dateString}`
    }
}

const regex = /^sensor\.replay_(.*)$/gm;
const match = regex.exec(msg.topic)

const room = match[1]

msg.payload = {
    group_name,
    state: msg.payload > 1 ? "on" : "off",
    data: {
        entity_id: `light.${msg.payload.room}`    
    }
}

return msg;

return typeof msg.payload === "object" 
    ? { payload: msg.payload.motion }
    : msg;

msg.group_name = msg.payload.group_name || env.get("group_name");
msg.payload = { entity_id: `light.${msg.group_name.toLowerCase().replace(/ /g, "_")}` }

return msg;

msg.payload = {
    data: {
        title: msg.error.source.name,
        data: { subtitle: msg.error.source.type },
        message: msg.error.message
    }
}

return msg;

const clamp = (num, min, max) => 
    Math.min(Math.max(num, min), max);

const motionCount = flow.get("motionCount")
const min = env.get("minDelay") || 0;
const max = env.get("maxDelay") || 15;

const on = msg.payload === true;
const delay = clamp(motionCount*motionCount, min, max) * 1000 * 60

return {
    ...msg,
    ...(on ? { reset: true } : { delay })
}

if (msg.payload === true) {
    const motionCount = flow.get("motionCount") || 0;

    flow.set("motionCount", motionCount + 1);
}

return { 
    ...msg
};

return {
    ...msg,
    payload: msg.payload === true 
        || msg.payload.motion === true 
        || msg.payload === "on"
}

const on = msg.payload === true;
const delay = msg.delay / 1000 / 60;

const action = !on ? `${delay}m` : "Stop"
const fill = !on ? "green" : "red";

const text = `${action} at: ${msg.dateString}` 


return {
    payload: {
        shape: "dot",
        fill,
        text
    }
}

return {
    payload: {
        shape: "dot",
        fill: "blue",
        text: `Finished at: ${msg.dateString}`
    }
}

const service = env.get("service");

const data = {
    ...env.get("data"),
    ...(
        (typeof msg.payload === "object" 
            && typeof msg.payload.data === "object")
        ? msg.payload.data
        : {}
    ),
};

const domain = service === "hue_activate_scene"
    ? "hue"
    : "light";

const entity_id = service === "hue_activate_scene"
    ? "light." + data.group_name.toLowerCase().replace(/ /g, "_")
    : env.get("entity_id");

flow.set("entity_id", entity_id);
flow.set("domain", domain);
flow.set("service", service);
flow.set("data", data);
flow.set("attempt", 0);

return msg;

const attempt = flow.get("attempt");

flow.set("attempt", attempt + 1);

return msg;

const entity_id = flow.get("entity_id");
const service = flow.get("service");

msg.payload = {
    data: {
        title: `Failed to update ${entity_id}`,
        message: `Could not perform ${service}`
    }
}

return msg;

msg.payload = typeof msg.payload === "object"
    ? msg.payload
    : {};

if (flow.get("service") === "hue_activate_scene") {
    msg.payload.data = flow.get("data");
} else {
    msg.payload.data = { 
        entity_id: flow.get("entity_id"),
        ...flow.get("data")
    };
}

msg.flow = {
    entity_id: flow.get("entity_id"),
    domain: flow.get("domain"),
    service: flow.get("service"),
    data: flow.get("data"),
    attempt: flow.get("attempt")
};
msg.env = {
    entity_id: env.get("entity_id"),
    service: env.get("service"),
    data: env.get("data"),
};

msg.payload.domain = flow.get("domain");
msg.payload.service = flow.get("service");

return msg;

const domain = flow.get("domain");
const service = flow.get("service");

const attempt = flow.get("attempt");
const attemptText = attempt > 0
    ? `#${attempt} `
    : "";

const color = attempt === 0 
    ? "green"
    : (attempt < 3
        ? "yellow"
        : "red")

msg.payload = {
    text: `${attemptText}${domain}.${service} called at: ${msg.dateString}`,
    shape:"dot", 
    fill: color,
}

return msg;

const dayPhase = msg.payload

const color = {
    Morning: "blue",
    Day: "green",
    Evening: "yellow",
    Night: "red"
}

const text = `${dayPhase} at: ${msg.dateString}` 


return {
    payload: {
        shape: "dot",
        fill: color[dayPhase],
        text
    }
}

const script = msg.payload[2]
const counters = script
    .substring(17,script.indexOf(";"))
    .replace(/'/g, '"')
    .replace(/,([^,]*)$/, '$1');


msg.payload = JSON.parse(counters);

return msg;
